h_zipcode= #{hZipcode}, h_address= #{hAddress}, h_ADDRESS_DETAIL= #{hAddressDetail},
h_originalname=#{hOriginalname}, h_filesize=#{hFilesize}, h_filename=#{hFilename}
where h_id = #{hId}

