mapper.insert:

	SELECT QA_SEQ.NEXTVAL FROM DUAL

INSERT INTO QA(
  QA_NO
, C_NO
, M_NO
, QA_TITLE
, QA_CONTENT
, QA_STEP
, QA_GROUP_ID
, QA_REGDATE
) VALUES (
  #{qaNo}
, #{cNo}
, #{mNo}
, #{qaTitle}
, #{qaContent}
, #{qaStep}
, #{qaGroupId}
, SYSDATE
)

mapper.update:
UPDATE QA
	SET
	   QA_TITLE=#{newsTitle}
	  ,QA_CONTENT=#{newsTitle}
	  ,QA_STEP=#{newsTitle}
	WHERE QA_NO=#{qaNo}
